mapper.select:
select

from



		and DEPT_CODE = #{pd.DEPT_BIANMA}


		and TABLE_CODE = #{pd.TABLE_CODE}


		and BILL_OFF = #{pd.BILL_OFF}


		and RPT_DUR = #{pd.RPT_DUR}

SELECT a.RPT_DUR, a.BILL_OFF, a.DEPT_CODE, a.TABLE_CODE, a.COL_CODE, a.COL_NAME, a.DISP_ORDER, a.DICT_TRANS, a.COL_HIDE, a.COL_SUM, a.COL_AVE, a.COL_TRANSFER, a.COL_FORMULA, a.CAL_ORDER, a.NUM_DGT, a.DEC_PRECISION, b.NAME DNAME, c.TABLE_NAME, d.DICT_NAME FROM tb_tmpl_config_detail a LEFT JOIN oa_department b on a.DEPT_CODE = b.DEPARTMENT_CODE LEFT JOIN tb_tmpl_config_base c on a.TABLE_CODE = c.TABLE_CODE LEFT JOIN tb_tmpl_config_dict d on a.DICT_TRANS = d.DICT_CODE
and a.DEPT_CODE = #{DEPARTMENT_CODE}
and a.TABLE_CODE = #{TABLE_CODE}
and a.BILL_OFF = #{BILL_OFF}
and a.RPT_DUR = #{RPT_DUR}
${filterWhereResult}
order by ${sidx} ${sord}

